$.tablesorter.addWidget({id:'staticRow',format:function(table){if(typeof $(table).data('hasSorted')=='undefined')
{$(table).data('hasSorted',!0);$('tbody .static',table).each(function(){$(this).data('tableindex',$(this).index())})}
else{var hasShuffled=!0;while(hasShuffled)
{hasShuffled=!1;$('tbody .static',table).each(function(){var targetIndex=$(this).data('tableindex')+1;if(targetIndex!=$(this).index())
{hasShuffled=!0;var thisRow=$(this).detach();var numRows=$('tbody tr',table).length;if(targetIndex>=numRows)
{thisRow.appendTo($('tbody',table))}
else if(targetIndex==0)
{thisRow.prependTo($('tbody',table))}
else{thisRow.insertBefore($('tbody tr:eq('+targetIndex+')',table))}}})}}}})